'  l_count number;',
'  l_old_locale  VISA_TYPES.LOCALE%type;',
'begin',
'  if :APEX$ROW_STATUS = ''U'' then',
'     select locale',
'     into l_old_locale',
'     from VISA_TYPES$i18n',
'     where rowid = :ROWID;',
'     ',
'     if l_old_locale != :LOCALE then',
'       select count(*) ',
'       into l_count',
'       from VISA_TYPES$i18n',
'       where locale = :LOCALE',
'       and Key_value = :P1410KEY_VALUE;',
'     else',
